

    function writeToPage() {

            var fn = document.getElementById("fname").value;
            var un = document.getElementById("mno").value;
            var em = document.getElementById("email").value;


            if (fn === "") {
        alert("Please fill out the  Name field.");
                document.getElementById("p1").style.color = "black";
                document.getElementById("p1").style.fontFamily = "Arial";
                document.getElementById("p1").style.fontSize = "larger";
            }
            else if (un === "") {
        alert("Please fill out the Mobile field.");
                document.getElementById("p2").style.color = "black";
                document.getElementById("p2").style.fontFamily = "Arial";
                document.getElementById("p2").style.fontSize = "larger";
            }
            else if (em === "") {
        alert("Please fill out email field.");
                document.getElementById("p3").style.color = "black";
                document.getElementById("p3").style.fontFamily = "Arial";
                document.getElementById("p3").style.fontSize = "larger";
            }

            else {

        document.getElementById("username").innerHTML = fn;

                document.getElementById("mobi").innerHTML = un;
                document.getElementById("pemail").innerHTML = em;
            }
        }


   